async function getCurrentLeaguePatch() {
    const url = "https://ddragon.leagueoflegends.com/api/versions.json";
    try {
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        const versions = await response.json();
        const currentPatch = versions[0]; // First version is the current patch
        var patchText = document.getElementById('patchText');
        if (patchText) {
            patchText.textContent = currentPatch;
        }
        return currentPatch;
    } catch (error) {
        console.error("Error fetching patch version:", error);
        var patchText = document.getElementById('patchText');
        if (patchText) {
            patchText.textContent = "Error";
        }
        return null;
    }
}
getCurrentLeaguePatch();
